var consumerRecord = sforce.connection.query("SELECT Loyalty_Total_Points__c from Account where id ='{!Account.Id}' "); 

var records = consumerRecord.getArray("records"); 

var loyaltyRecord = sforce.connection.query("SELECT Name from Loyalty__c where Loyalty_Consumer__c ='{!Account.Id}' "); 

var loyaltyQuery = loyaltyRecord.getArray("records"); 

var totalLoyaltyPoints = records[0].Loyalty_Total_Points__c.valueOf();

if (typeof loyaltyQuery !== 'undefined' && loyaltyQuery.length > 0){

window.location.href= "https://jdec.my.salesforce.com/a0V/e?CF00N0Y00000SBtgw={!Account.Name}&CF00N0Y00000SBtgy="+loyaltyQuery[0].Name+"&CF00N0Y00000SBtgw_lkid={!Account.Id}&00N1l000000FpcR="+Math.floor( totalLoyaltyPoints ) +"&saveURL=%2F{!Account.Id}&retURL=%2F{!Account.Id}"
}else{
alert("Not a valid Loyalty Consumer records. Please contact your system administrator");
}
